;}, getSubId: function(fn) {this.listeners.push(fn);}, ready: function(fn) {this.listeners.push(fn);} };}(function(){var a=document.createElement('script');a.type='application/javascript';a.async=true;a.src='#root_path#js/k.min.js';var s=document.getElementsByTagName('script')[0];s.parentNode.insertBefore(a,s)})();\n</script><noscript><img height='0' width='0' alt='' src='#internal_url#'/></noscript>", "postback_code" => "<script>KTracking.reportConversion(revenue, 'lead', {sub_id_1: 'johh@gmail.com', sub_id_2: 'John Smith'})</script>"], ["id" => "pixel", "name" => ["ru" => "Пиксель для Email-рассылок", "en" => "Pixel for Email"], "group" => "other", "instructions" => ["ru" => "\n            <h3>О методе</h3>\n            <p>Код позволяет считать открытия писем. Делается это через загрузку картинки.</p>\n            <h3>Как использовать?</h3>\n            <p>Создайте один поток с действием \"CURL\" и укажите адрес какой-нибудь картинки (логотип, скриншот и 1x1 пиксель).\n            В шаблоне письма разместите HTML код.\n            ", "en" => "\n            <h3>About</h3>\n            <p>That code creates an img tag. When user load the image you'll get information about it.</p>\n            <h3>How to use?</h3>\n            <p>Create one stream with action \"CURL\" and enter URL of any image (a logo, screenshot or 1x1 pixel).\n            Then use the code in your email templates.</p>\n            "], "code" => "<img height='1' width='1' alt='' src='#internal_url#'/>", "add_params" => "frm=img"], ["id" => "click_client", "beta" => true, "edition" => "pro", "name" => ["ru" => "KClient PHP", "en" => "KClient PHP"], "group" => "other", "instructions" => ["ru" => "\n            <h3>О методе</h3>\n            <p>Клиент для работы с Click API позволяет обращаться к кампании на стороне сервера.</p>\n            <h3>Как использовать?</h3>\n            <p>Скачайте файл <a href=\"?object=codePresets.downloadClient\">kclient.php</a> и положите его в директорию вашего сайта. </p>\n            <p>Затем скопируйте код и вставьте в шаблон сайта, в самом начале шаблона, до &lthtml> и DOCTYPE</p>\n            <h3>Как создать ссылку на оффер</h3>\n            <p>Для этого используйте метод <code>\$client->getOffer()</code> или <code>\$client->getOffer(ID)</code> для ссылки на определенный оффер.</p>\n            <p><pre><code>" . htmlentities("<a href=\"<?= \$client->getOffer() ?>\">link</a>") . "</code></pre></p>\n            <h3>Документация</h3>\n            <p><a target=\"_blank\" href=\"http://v9.help.keitaro.io/ru/click-api-client\">http://v9.help.keitaro.io/ru/click-api-client</p>\n            ", "en" => "\n            <h3>About</h3>\n            <p>Click Client allows you to use Click API, so you can make requests to the tracker and just show the result to the visitors.</p>\n            <h3>How to use?</h3>\n            <p>Download file <a href=\"?object=codePresets.downloadClient\">kclient.php</a> and upload it to website directory. </p>\n            <p>Copy and paste the code below to your website template (before tags &lthtml> and DOCTYPE)</p>\n            <h3>How to make offer link?</h3>\n            <p>To do that use method <code>\$client->getOffer()</code> or <code>\$client->getOffer(ID)</code> to specify offer ID.</p>\n            <p><pre><code>" . htmlentities("<a href=\"<?= \$client->getOffer() ?>\">link</a>") . "</code></pre></p>\n            <h3>Documentation</h3>\n            <p><a target=\"_blank\" href=\"http://v9.help.keitaro.io/en/click-api-client\">http://v9.help.keitaro.io/en/click-api-client</p>\n            "], "code" => "<?php\n// Query params are: #params# \n\nrequire_once dirname(__FILE__) . '/kclient.php';\n\$client = new KClient('#api_path#', '#token#');\n\$client->sendAllParams();       // to send all params from page query\n\$client->forceRedirectOffer();       // redirect to offer if an offer is chosen\n// \$client->param('sub_id_5', '123'); // you can send any params\n// \$client->keyword('PASTE_KEYWORD');  // send custom keyword\n// \$client->currentPageAsReferrer(); // to send current page URL as click referrer\n// \$client->disableSessions(); // to disable using session cookie (without this cookie restoreFromSession wouldn't work)\n// \$client->debug();              // to enable debug mode and show the errors\n// \$client->execute();             // request to api, show the output and continue\n\$client->executeAndBreak();     // to stop page execution if there is redirect or some output\n?>", "offer_code" => "<?php\nrequire_once dirname(__FILE__) . '/kclient.php';\n\$client = new KClient('#api_path#', '#token#');\n\$client->restoreFromQuery();\n?>", "postback_code" => "<?php\nrequire_once dirname(__FILE__) . '/kclient.php';\n\$client = new KClient('#api_path#', '#token#');\n\$client->restoreFromSession();\n\n// Send postback \n\$params = array(\"subid\" => \$client->getSubId(), \"status\" => \"lead\", \"payout\" => 0);\n\$ch = curl_init();\ncurl_setopt(\$ch, CURLOPT_URL, \"#postback_path#\");\ncurl_setopt(\$ch, CURLOPT_RETURNTRANSFER, 1);\ncurl_setopt(\$ch, CURLOPT_POSTFIELDS, http_build_query(\$params));\ncurl_setopt(\$ch, CURLOPT_POST, 1);\n\$output = curl_exec(\$ch); \n// echo \$output; \ncurl_close(\$ch); \n?>"], ["id" => "tracking_kclient_js", "name" => ["ru" => "KClient JS", "en" => "KClient JS"], "group" => "other"], ["id" => "wp", "edition" => "pro", "name" => ["ru" => "WordPress", "en" => "WordPress"], "group" => "other", "instructions" => ["ru" => "\n                <h3>Установка плагина в WordPress</h3>\n                <ol>\n                    <li>Зайдите в \"Плагины > Установить\";</li>\n                    <li>Установите плагин \"Keitaro Tracker Integration\";</li>\n                    <li>Скопируйте блок настроек, которые видите ниже;</li>\n                    <li>В WordPress откройте страницу Keitaro;</li>\n                    <li>Нажмите \"Import settings\" и вставьте блок настроек.</li>\n                    <li>Нажмите \"Save Changes\".</li>\n                </ol>\n                \n                О настройках плагина, подготовке лендингов, офферов и отправке постбека читайте на странице <a target=\"_blank\" href=\"http://v9.help.keitaro.io/en/integration#Wordpress\">http://v9.help.keitaro.io/en/integration#Wordpress</a>\n                ", "en" => "\n                <h3>How to install Keitaro to WordPress</h3>\n                <ol>\n                    <li>Open \"Plugins > Add New\".</li>\n                    <li>Install plugin \"Keitaro Tracker Integration\".</li>\n                    <li>Copy settings block that you see below.</li>\n                    <li>Open page \"Keitaro\" in WordPress</li>\n                    <li>Press button \"Import settings\" and paste the block.</li>\n                    <li>Press \"Save Changes\".</li>\n                </ol>\n                \n                Read about how prepare landing pages, offers, send postbacks on page <a target=\"_blank\" href=\"https://help.keitaro.io/en/wp-plugin\">https://help.keitaro.io/en/wp-plugin</a>\n                "]]];

